import React from 'react'
import '../../App.css';
import data from './ProductLandingPageData.json';
import data2 from './Zerodebug_Productbox.json';
import Navbar from 'react-bootstrap/Navbar';

export default class ProductLandingPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            page_id: props.page_id,
            submenu_id: props.submenu_id,
            data: data
        };
    }

    play_video() {
    }

    render() {
        let submenu_id = this.state.submenu_id
        let page_id = this.state.page_id;
        const data = this.state.data;

        const products = data.products;
        const products2 = data2.products;
        var product;
        var store_list;
        var desc_container;
        var first_container;
        var second_container;
        var zerodebug_productboxes;
        var intro_text_field;
        var video_field;

        var add_id = "";
        for (var i = 0; i < products.length; ++i) {
            var product_i = products[i];
            if (product_i.app_id == page_id) {
                product = product_i;
                break;
            }
        }

        if (!product) {
            return (
                <ul class="navbar-nav w-100 justify-content-start">
                    <li class="nav-item active">
                        <a class="nav-link" href="https://www.zerodebug.com">no page found</a>
                    </li>
                </ul>
            );
        }

        store_list = product.intro_field.app_store.map(store_item => (
            <li class="list-group-item flex-fill"><a class="store-button btn btn-outline-secondary btn-block." href={store_item.url}><i class="fa fa-apple" aria-hidden="true"></i> {store_item.name}</a></li>
        ))

        zerodebug_productboxes = product.overview_container.map((a_product, i) => {
            let class_def = "text-center col product_row_box";
            let second_icon;
            if (i === 0) {
                class_def = "text-center col product_row_box background_color_accent";
            }
            else if (i === 1) {
                class_def = "text-center col product_row_box background_color_accent_sec";
            }
            else if (i === 2) {
                class_def = "text-center col product_row_box background_color_accent_third";
            }
            else if (i === 3) {
                class_def = "text-center col product_row_box background_color_accent_fourth";
            }
            if (a_product.second_icon_source) {
                second_icon = (<img class="img_color_black" src={a_product.second_icon_source} width="80px"></img>)
            }
            return (
                <div class={class_def} >
                    <a href={a_product.heading}>
                        <div class="inner-product-box">
                            <h4 class="text_color_black">
                                {a_product.heading}
                            </h4>
                            <div class="bottom_small_margin">
                                <div class="product_box_desc text_color_black">
                                    {a_product.description}
                                </div>
                            </div>
                            <img class="img_color_black" src={a_product.icon_source} width="80px"></img>
                            {second_icon}
                        </div>
                    </a>
                </div>
            )
        })




        var first_subcontainer;

        //if (product.app_id != "zerodebug") {
        first_subcontainer = (
            <div class="text-block-main-right">
                <h1>{product.intro_field.sub_intro_text}</h1>
            </div>
        )
        //}

        first_container = (<div class="position-relative overflow-hidden text-center container-main">
            <div class="">
                <img src={product.intro_field.image_source} width="100%"></img>
                <div class="text-block-main ">
                    <div class="text-block-main2" style={{ marginTop: "100px" }}>
                        <img src={product.icon_source} width="50"></img>
                        <h1 class="display-4 display-44">{product.app_name_string}</h1>
                        <p class="lead  uniform_big_margin">{product.intro_field.intro_text}</p>
                        <div class="store-icon-list">
                            <ul class="list-group list-group-horizontal">
                                {store_list}
                            </ul>
                        </div>
                    </div>
                </div>
                {first_subcontainer}
            </div>
        </div>)

        second_container = (
            <div class="text-center"  style={{backgroundColor:"#C0C0C0",color:"black",padding:"50px"}}>
                        <h1 class="text-center" >Our Apps</h1>
                <div class="row product_row text-center" >
                    {zerodebug_productboxes}
                </div>
            </div>)


        if (product.app_id != "zerodebug") {
            intro_text_field = (
                <div class="position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center ">
                    <div>
                        <h1 class="display-4 font-weight-normal"><img src={product.intro_text.intro_icon} width="50px"></img>{product.intro_text.intro_heading}</h1>
                        <p class="justify">{product.intro_text.intro_text}</p>
                    </div>
                </div>
            )
        }

        desc_container = product.desc_container.map((store_item, i) => {

            var dark_mode = ""
            if (i % 2 === 0) {
                //return (<div class="position-relative overflow-hidden p-3 p-md-5 m-md-3 bg-dark desc_box_sdsdf">
                //<h3 class="display-4 font-weight-normal text-right">{store_item.heading}</h3>

                return (
                <div class="container-fluid" style={{height:"100%"}}>
                <div class="row align-items-center" style={{backgroundColor:"orange",height:"100%"}}>
                  <div class="col-4"  style={{backgroundColor:"orange",height:"100%"}}>
                    <div class="row">
                      <div class="col-6" style={{backgroundColor:"green"}}>
                      I'm the first element in the nested grid
                      </div>
                      <div class="col-6" style={{backgroundColor:"blue"}}>
                      I'm the second element in the nested gridhjhlhjlhlhljhlhjlhjlhljhljhkjhkjhkgjhfjfg
                      </div>
                    </div>
                    <div class="row">
                      <div class="col-6" style={{backgroundColor:"blue"}}>
                      I'm the first easdasdasdaslement in the nested grid
                      </div>
                      <div class="col-6" style={{backgroundColor:"red"}}>
                      I'm the second element in the nested gridhjhlhjlhlhljhlhjlhjlhljhljhkjhkjhkgjhfjfgipoispodaispdoaisdpaosidpaosidpasodiaspdoispodiaspdoispodispdoipsoidposidpsoidpoisdpoi
                      </div>
                    </div>
                    <div class="row">
                      <div class="col-6" style={{backgroundColor:"orange"}}>
                      I'm the first element in the nested grid
                      </div>
                      <div class="col-6" style={{backgroundColor:"yellow"}}>
                      I'm the second element in the nested gridhjhlhjlhlhljhlhjlhjlhljhljhkjhkjhkgjhfjfg
                      </div>
                    </div>
                    <div class="row">
                      <div class="col-6" style={{backgroundColor:"green"}}>
                      I'm the first element in the nested grid
                      </div>
                      <div class="col-6" style={{backgroundColor:"blue"}}>
                      I'm the second element in the nested gridhjhlhjlhlhljhlhjlhjlhljhljhkjhkjhkgjhfjfg
                      </div>
                    </div>
                  </div>
                  <div class="col-8" style={{backgroundColor:"red"}}>
                  <aside >
                            <img src={store_item.image_source} width="100%"></img>
                            <h2 class="text-center">{store_item.heading}</h2>
                        </aside>

                  </div>
                </div>
              </div>

              );
            }
            else {
                return (<div class="position-relative overflow-hidden">
                    <div class={store_item.image_class} id="foo">
                        <asider >
                            <img src={store_item.image_source} width="100%"></img>
                            <h2 class="text-center">{store_item.heading}</h2>
                        </asider>
                    </div>
                    <div class={store_item.text_class}>
                        <div class="mh-100 container" style={{ backgroundColor: "" }}>
                            <div class="container" style={{ backgroundColor: "" }}>

                                <ul class="col ul_text_left" height="100%" >
                                    {store_item.list_items.map(list_item => (
                                        <div class="col top_big_margin">
                                            {list_item}
                                        </div>
                                    ))}
                                </ul>

                            </div>

                        </div>
                    </div>
                </div>);
            }
        });


        video_field = (
            <div class="videoWrapper">
                <iframe width="560" height="349" src={product.intro_video.src_url} frameborder="0" allowfullscreen></iframe>
            </div>
        )

        //                {intro_text_field}

        return (
            <div class="main">
                {first_container}
                {second_container}
                {video_field}
                {desc_container}
            </div>
        );
    }
}
