import React from 'react'
import '../../App.css';
import data from './ProductLandingPageData.json';

export default class  ProductLandingPage extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            page_id: props.page_id, 
            submenu_id: props.submenu_id,
            data: data
        };
      }


    render() {
        let submenu_id = this.state.submenu_id
        let page_id = this.state.page_id;
        const data = this.state.data;

        const products = data.products;
        var product;
        var add_id = "";
        for(var i=0; i < products.length; ++i) {
            var product_i = products[i];
            if(product_i.app_id == page_id) {
                product = product_i;
                break;
            }
        }

        if (!product) {
            return ( 
                <ul class="navbar-nav w-100 justify-content-start">
                <li class="nav-item active">
                    <a class="nav-link" href="https://www.zerodebug.com">no page found</a>
                </li>
                </ul>
            );
        }



        if (this.state.page_id == "touchAble") {
        return (
            <div class="main">
            <div class="position-relative overflow-hidden p-md-5 text-center">
                <div class="container-main">
                    <img src="./touchable_landing.png" width="100%"></img>
                    <div class="text-block-main">
                        <div class="text-block-main2">
                            <img src="https://zerodebug.com/images/ta_logo_2048.png" width="50"></img>
                            <h1 class="display-4 display-44 font-weight-normal">touchAble Pro</h1>
                            <p class="lead font-weight-normal">total control over Ableton Live with your mobile devices</p>
                            <div class="store-icon-list">
                                <ul class="list-group list-group-horizontal">
                                    <li class="list-group-item flex-fill"><a class="store-button btn btn-outline-secondary btn-block." href="#"><i class="fa fa-apple" aria-hidden="true"></i>  iOS</a></li>
                                    <li class="list-group-item flex-fill"><a class="store-button btn btn-outline-secondary btn-block." href="#"><i class="fa fa-android" aria-hidden="true"></i>  Android</a></li>
                                    <li class="list-group-item flex-fill"><a class="store-button btn btn-outline-secondary btn-block." href="#"><i class="fa fa-windows" aria-hidden="true"></i>  Windows</a></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                    <div class="text-block-main-right">
                        <div class="text-block-main2">
                            <p class="lead font-weight-normal">sonic bla</p>
                        </div>
                    </div>
                </div>
            </div>
            <div class="position-relative overflow-hidden p-3 p-md-5 m-md-3 text-center bg-dark">
                <div>
                    <h1 class="display-4 font-weight-normal">everything you need directly at your fingertips</h1>
                    <p class="justify">Eight years ago touchAble defined what a touch controller app for Live could do. touchAble Pro has been reimagined and recoded from the ground up, incorporating years of feedback from musicians at every level.
                        touchAble Pro provides a cleaner interface, allows much more powerful customization and gives you the deepest access possible to Ableton Live.

                        From waveform views to editors to automations - with touchAble Pro, you now have the complete Ableton Live experience literally at your fingertips. You can perform and edit without ever touching a mouse, you can customize your experience to access exactly the controls you need to edit or play. You’ll gain complete control that’s as portable as the smartphone, tablet or laptop you’re carrying with you.
                    </p>
                </div>
            </div>
            <div class="position-relative overflow-hidden p-3 p-md-5 m-md-3">
                <p>&nbsp;</p>
                <p></p>
                <center>
                    <div class="embed-container">
                        <iframe width="1000" height="500"  src="https://www.youtube.com/embed/H6EnpjfztNw?rel=0" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen=""></iframe>
                    </div>
                </center>
            </div>
            <div class="position-relative overflow-hidden p-3 p-md-5 m-md-3 bg-dark">
                <div class="desc-text-left">
                    <h3 class="display-4 font-weight-normal">full control</h3>
                    <ul>
                        <li>  Eight years ago touchAble  Eight years ago touchAble  Eight years ago touchAble  Eight years ago touchAble  </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>   Eight years ago touchAble </li> 
                    </ul>
                </div>
                <div class="desc-image-right">
                    <img src="./touchable_landing.png" width="100%"></img>
                </div>
            </div>
            <div class="position-relative overflow-hidden p-3 p-md-5 m-md-3">
                <div class="desc-image-left">
                    <img src="./touchable_landing.png" width="100%"></img>
                </div>
                <div class="desc-text-right">
                    <h3 class="display-4 font-weight-normal">full control</h3>
                    <ul>
                        <li>  Eight years ago touchAble   </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>  Eight years ago touchAble  </li> 
                        <li>   Eight years ago touchAble </li> 
                    </ul>
                </div>

            </div>
        </div>
            );
        }
        else {
            return (
<div class="main">
</div>
            );
        }
}
