import React from 'react'
import '../../App.css';
import data from './ProductLandingPageData.json';
import Navbar from 'react-bootstrap/Navbar';

export default class ProductDescriptionBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            product_number: props.product_number,
            page_id: props.page_id,
            submenu_id: props.submenu_id,
            store_item: props.store_item,
            heading_text: "asdasd",
            image_source: props.store_item.image_source
        };
    }

    change_image(heading,image_source_path) {
        this.state.heading_text = heading;
        this.setState({ heading_text: heading, image_source: image_source_path});
      }

    render() {

        let inner_content = this.state.store_item.list_items.map(list_item => {
            return (
            <div class="col-12 product_detail_box px-md-l2" style={{ padding: "0px" }}>
                <div class="row" onMouseOver={this.change_image.bind(this, list_item.description,list_item.image_source)}>
                    <div class="col-8 inner-product-box" style={{ height:"100%"}}>

                        <h3 class="text_color_black">
                            {list_item.heading}
                        </h3>
                        <div class="bottom_small_margin">
                            <div class="product_box_desc text_color_black">
                                {list_item.description}
                            </div>
                        </div>

                    </div>
                    <div class="col-4 image_bottom" style={{}}>
                        <img class="img_color_black" src={list_item.icon_source} height="40px"></img>
                    </div>
                </div>

            </div>
            )
        })

        let image_content = (
            <div class="col-8 align-self-end" style={{ margin: "0px", padding: "0px" }}>
                <img src={this.state.image_source} width="100%"></img>
                <div class="text-block-main-bottom">
                <h2>{this.state.heading_text}</h2>
            </div>
            </div>
        )

        let box_content = (
        <div class="col-4" >
            <div class="row align-items-center" style={{ marginRight: "10px", padding: "0px" }}>
                {inner_content}
            </div>
        </div>
        )

        let desc_container;

        if (this.state.product_number % 2 !== 0) {
            desc_container = (
                <div class="container-fluid " style={{ backgroundColor: "#484848", color: "white", paddingBottom: "50px", paddingTop: "50px" }}>
                <h1 class="text-center" style={{backgroundColor:"", color:"white"}}>{this.state.store_item.heading}</h1>
                    <div class="row top_big_margin bottom_big_margin align-items-center">
                        {image_content}
                        {box_content}
                    </div>
                </div>)
        }
        else {
            desc_container = (
                <div class="container-fluid " style={{ backgroundColor: "#181818", color: "white", paddingBottom: "50px", paddingTop: "50px" }}>
                <h1 class="text-center" style={{backgroundColor:"", color:"white"}}>{this.state.store_item.heading}</h1>
                    <div class="row top_big_margin bottom_big_margin align-items-center">
                        {box_content}
                        {image_content}
                    </div>
                </div>)
        }

        return (
            <div>
                {desc_container}
            </div>
        );



    }
}
