                </li>
                </ul>
            );
        }

    store_list = product.intro_field.app_store.map(store_item => (
    <li class="list-group-item flex-fill"><a class="store-button btn btn-outline-secondary btn-block." href={store_item.url}><i class="fa fa-apple" aria-hidden="true"></i> {store_item.name}</a></li>
    ))



    if (product.app_id != "zerodebug") {
        first_container = ( <div class="position-relative overflow-hidden p-md-5 text-center">
                <div class="container-main">
                    <img src={product.intro_field.image_source} width="100%"></img>
                    <div class="text-block-main">
                        <div class="text-block-main2">
                            <img src={product.icon_source} width="50"></img>
                            <h1 class="display-4 display-44 font-weight-normal">{product.app_name_string}</h1>
                            <p class="lead font-weight-normal">{product.intro_field.intro_text}</p>
                            <div class="store-icon-list">
                                <ul class="list-group list-group-horizontal">
                                        {store_list}
                                </ul>
                            </div>
                        </div>
                    </div>
                    <div class="text-block-main-right">
                        <div class="text-block-main2">
                            <p class="lead font-weight-normal">{product.intro_field.sub_intro_text}</p>
                        </div>
                    </div>
                </div>
            </div>)
    }
    else {
        first_container = ( <div class="position-relative overflow-hidden p-md-5 text-center">
                <div class="container-main">
                    <img src="https://zerodebug.com/images/ta_logo_2048.png" width="50px"></img>
                    <img src="./studiomux_icon_web.png"></img>
<img src="./modstep_icon_web.png" width="50px"></img>
<img src="./modstep_icon_web.png" width="50px"></img>

    if (app_id == "touchAble") {
        //content = <img src="https://zerodebug.com/images/ta_logo_2048.png" width="30"></img>
    }
        else if (app_id == "modstep") {
        //content = <img src="./modstep_icon_web.png" width="30"></img>
    }
        else if (app_id == "soda") {
        //content = <img src="./soda_icon_web.png" width="30"></img>
    }
       else if (app_id == "studiomux") {
        //content = <img src="./studiomux_icon_web.png" width="30"></img>
    }
    else {
        //content = <img src="./logo_zerodebug_white.png" width="30"></img>
    }

                </div>
            </div>)
    }

    desc_container = product.desc_container.map((store_item,i) => {
        var dark_mode = ""
    if (i % 2 === 0) {
          return (<div class="position-relative overflow-hidden p-3 p-md-5 m-md-3 bg-dark">
        <div class={store_item.text_class}>
            <h3 class="display-4 font-weight-normal">{store_item.heading}</h3>
            <ul class="ul_text_left">
            {store_item.list_items.map(list_item => (
                    <li> {list_item} </li> 
            ))}
            </ul>
        </div>
        <div class={store_item.image_class}>
            <img src={store_item.image_source} width="100%"></img>
        </div>
    </div>);
    }
    else {return (<div class="position-relative overflow-hidden p-3 p-md-5 m-md-3">
        <div class={store_item.image_class}>
            <img src={store_item.image_source} width="100%"></img>
