import React from 'react'
import '../../App.css';
import ProductLandingPage from './ProductLandingPage'


export default class  PageContent extends React.Component{

    constructor(props) {
        super(props);
        this.state = {page_id: props.page_id, submenu_id: props.submenu_id};
      }


    render() {
        let page_id = this.state.page_id;
        let submenu_id = this.state.submenu_id;

        if (this.state.submenu_id == "landing_page") {
        return (
            <ProductLandingPage page_id ={page_id} submenu_id={submenu_id} />
            );
        }
        else {
            return (
        <div class="main">
        </div>
            );
        }
}
}
