import React from 'react'
import { render } from 'react-dom'
import '../../App.css';
import Nav from 'react-bootstrap/Nav';
import { MDBIcon, MDBContainer, MDBBtn } from 'mdbreact';
import data from './SubNavbarData.json';


export default class SubNavbar extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      page_id: props.page_id,
      submenu_id: "touchAble",
      data: data
    };
  }


createSubMenus(){
    let submenu_id = this.state.submenu_id
    let page_id = this.state.page_id;
    let content;

    return (

         <li class="nav-item active">
            <a class="nav-link" onClick={submenu_id = "manual", this.props.selectSubPage.bind(this,submenu_id)} href="#">manual</a>
        </li>


    )
}

createNavbar(props){
    let submenu_id = this.state.submenu_id
    let page_id = this.state.page_id;
    let content;

    if (page_id == "touchAble") {
    content = <img src="https://zerodebug.com/images/ta_logo_2048.png" width="30"></img>
}
    else if (page_id == "modstep") {
    content = <img src="./modstep_icon_web.png" width="30"></img>
}
    else if (page_id == "soda") {
    content = <img src="./soda_icon_web.png" width="30"></img>
}
   else if (page_id == "studiomux") {
    content = <img src="./studiomux_icon_web.png" width="30"></img>
}
else {
    content = <img src="./logo_zerodebug_white.png" width="30"></img>
}

return (
<nav class="navbar fixed-top-2 navbar-dark bg-dark navbar-expand-md bg-faded justify-content-center">
    <a class="navbar-brand" href="#" onClick={submenu_id = "landing_page", this.props.selectSubPage.bind(this,submenu_id)}>
        {content}
    </a>  
    <ul class="navbar-nav w-100 justify-content-start">
    <createSubMenus/>
    </ul>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsingNavbar3">
        <span class="navbar-toggler-icon"></span>
    </button>
    <div class="navbar-collapse collapse w-100" id="collapsingNavbar3">

        <ul class="nav navbar-nav ml-auto w-100 justify-content-end">
            <li class="nav-item">
                <a class="nav-link" href="https://www.facebook.com/touchableapp/" title="Facebook" target="_blank"><i class="fa fa-facebook" aria-hidden="true"></i></a>
            </li>
            <li class="nav-item">
                <a class="nav-link" href="https://www.twitter.com/touchableapp/" title="Twitter" target="_blank"><i class="fa fa-twitter" aria-hidden="true"></i></a>
            </li>
            <li class="nav-item">
                <a class="nav-link" href="https://www.instagram.com/touchableapp/" title="Instagram" target="_blank"><i class="fa fa-instagram" aria-hidden="true"></i></a>
            </li>
            <li class="nav-item">
                <a class="nav-link" href="https://www.instagram.com/touchableapp/" title="Instagram" target="_blank"><i class="fa fa-youtube" aria-hidden="true"></i></a>
            </li>
        </ul>
    </div>
</nav>
)
}

render() {
return (

[this.createNavbar()]

);
}

}
