import React from 'react'
import '../../App.css';

export default class ProductOverviewContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            product: props.product,
        };
    }


    render() {

        if (!this.state.product) {
            return (
                <ul class="navbar-nav w-100 justify-content-start">
                    <li class="nav-item active">
                        <a class="nav-link" href="https://www.zerodebug.com">no page found</a>
                    </li>
                </ul>
            );
        }



        let zerodebug_productboxes = this.state.product.overview_container.map((a_product, i) => {
            let class_def = "text-center col-sm product_row_box";
            let second_icon;
            if (i === 0) {
                class_def = "text-center col-sm product_row_box background_color_accent";
            }
            else if (i === 1) {
                class_def = "text-center col-sm product_row_box background_color_accent_sec";
            }
            else if (i === 2) {
                class_def = "text-center col-sm product_row_box background_color_accent_third";
            }
            else if (i === 3) {
                class_def = "text-center col-sm product_row_box background_color_accent_fourth";
            }
            if (a_product.second_icon_source) {
                second_icon = (<img class="img_color_black" src={a_product.second_icon_source} width="80px"></img>)
            }
            return (
                <div class={class_def} >
                    <a href={a_product.heading}>
                        <div class="inner-product-box">
                            <h4 class="text_color_black">
                                {a_product.heading}
                            </h4>
                            <div class="bottom_small_margin">
                                <div class="product_box_desc text_color_black">
                                    {a_product.description}
                                </div>
                            </div>
                            <img class="img_color_black" src={a_product.icon_source} width="80px"></img>
                            {second_icon}
                        </div>
                    </a>
                </div>
            )
        })

        let second_container = (
            <div class="text-center" style={{ backgroundColor: "#C0C0C0", color: "black" }}>
                <h1 class="text-center" >Our Apps</h1>
                <div class="row product_row text-center my-5" >
                    {zerodebug_productboxes}
                </div>
            </div>
            )


        return (
            <div class="">
                {second_container}
            </div>
        );
    }
}
