import React from 'react'
import '../../App.css';
import data from './ProductLandingPageData.json';
import Navbar from 'react-bootstrap/Navbar';

export default class ProductDescriptionBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            product_number: props.product_number,
            page_id: props.page_id,
            submenu_id: props.submenu_id,
            store_item: props.store_item
        };
    }

    render() {
        let submenu_id = this.state.submenu_id
        let page_id = this.state.page_id;
        const data = this.state.data;

        const products = data.products;
        var product;
        var desc_container;


        let inner_content = this.state.store_item.list_items.map(list_item => {
            return (<div class="col-6 product_detail_box" style={{ margin: "0px" }}>
                <a href={list_item.heading}>
                    <div class="inner-product-box">
                        <h4 class="text_color_black">
                            {list_item.heading}
                        </h4>
                        <div class="bottom_small_margin">
                            <div class="product_box_desc text_color_black">
                                {list_item.description}
                            </div>
                        </div>
                        <img class="img_color_black" src={list_item.icon_source} height="40px"></img>
                    </div>
                </a>
            </div>
            )
        })

        let image_content = (
            <div class="col-8" style={{ margin: "0px", padding: "0px" }}>
                <img src={this.state.store_item.image_source} width="100%"></img>
            </div>
        )

        let box_content = (<div class="col-4" >
            <div class="row" style={{ margin: "5px" }}>
                {inner_content}
            </div>
        </div>
        )

        let outer_content;

        if (this.state.product_number % 2 === 0) {

            outer_content = (
                <div class="container-fluid " style={{ backgroundColor: "#C0C0C0", color: "black", paddingBottom: "50px", paddingTop: "50px" }}>
                    <h1 class="text-center">{this.state.store_item.heading}</h1>
                    <div class="row align-items-center top_big_margin bottom_big_margin">
                        {box_content}
                        {image_content}
                    </div>
                </div>)
        }
        else {
            outer_content = (
                <div class="container-fluid " style={{ backgroundColor: "#C0C0C0", color: "black", paddingBottom: "50px", paddingTop: "50px" }}>
                    <h1 class="text-center">{this.state.store_item.heading}</h1>
                    <div class="row align-items-center top_big_margin bottom_big_margin">
                    {image_content}
                    {box_content}
                    </div>
                </div>)
        }

        return (
            <div>
                {outer_content}
            </div>
        );



    }
}
