import React from 'react'
import '../../App.css';

export default class TouchableInstallerContainer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            product_number: props.product_number,
            page_id: props.page_id,
            submenu_id: props.submenu_id,
            store_item: props.store_item,
            heading_text: "Multiplatform",
            image_source: props.store_item.image_source
        };
    }

    change_image(heading, image_source_path) {
        this.state.heading_text = heading;
        this.setState({ heading_text: heading, image_source: image_source_path });
    }

    render() {

        let heading_text = this.state.heading_text;

        const styles = {
            container: [
                {
                    backgroundColor: "var(--accent-bg-color)"
                },
                {
                    backgroundColor: "var(--secondary-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--third-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fourth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fifth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--sixth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--seventh-accent-bg-color)"
                },
            ]
        };


        let inner_content = this.state.store_item.list_items.map((list_item, i) => {
        return (
            <div class="col-5 col-sm-12 product_detail_box m-1" style={styles.container[i]}>
                <div class="row align-items-center product_detail_box py-0" onClick={this.change_image.bind(this, list_item.heading, list_item.image_source)}>
                    <div class="col-12 col-sm-10 p-0 p-sm-3 align-items-start text-center text-sm-left inner-product-box">
                        <h3 class="text_color_black align-items-start">
                            {list_item.heading}
                        </h3>
                    </div>
                    <div class="col-12 col-sm-2 align-items-center" style={{}}>
                        <img class="img_color_black align-items-center" src={list_item.icon_source} height="20px"></img>
                    </div>
                </div>

            </div>
        )
    })


    let inner_image_content;

    if (heading_text === "Video Guide") {
        inner_image_content =  (
        <div class="videoWrapper">
        <iframe width="560" height="349" src="https://www.youtube.com/embed/ZCfKtxPxwAc" frameborder="0" allowfullscreen></iframe>
        </div>
    )
    }
    else if (heading_text === "Downloads"){

        inner_image_content =(
            <section id="block-1">
            <div class="callout text-left">
                <center>
                    <div class="embed-container"><iframe width="560" height="315" src="https://www.youtube.com/embed/ZCfKtxPxwAc" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>
                    </div>
                    <h3>touchAble Pro – Windows Setup Video</h3>
                </center>
            </div>
            <div class="callout text-center">
                <h3>First Step: Download server and prerequisites</h3>
                <h4>Please download our latest server package for the system you use (32bit or 64bit) for Ableton Live</h4>

                <a href="https://zerodebug.com/server/touchAble_428_x64.exe"><h3>For Windows (64bit):
            <img src="https://zerodebug.com/images/exe.png" alt="exe-icon" class="clipped-image" height="30"></img> Installerpackage v.428 (64bit)</h3></a>

                <a href="/server/touchAble_428_x86.exe"><h3>For Windows (32bit):
<img src="https://zerodebug.com/images/exe.png" alt="exe-icon" class="clipped-image" height="30"></img>Installerpackage v.428 (32bit)</h3></a>

                <h3><a href="https://www.tobias-erichsen.de/software/rtpmidi.html">rtpMIDI <img src="https://zerodebug.com/images/exe.png" alt="exe-icon" class="clipped-image" height="30"></img> for Win64 and Win32</a></h3>

                <h3><a href="https://www.apple.com/itunes/download/win64">iTunes
            <img src="https://zerodebug.com/images/exe.png" alt="exe-icon" class="clipped-image" height="30"></img> for Windows (64bit)</a></h3>

                <h3><a href="https://www.apple.com/itunes/download/win32">iTunes <img src="https://zerodebug.com/images/exe.png" alt="exe-icon" class="clipped-image" height="30"></img> for Windows (32bit)</a></h3>



                <h4>Important: Download iTunes via the link above - don't use the version from the Windows Store!</h4>
            </div>

            <div class="call">

                <h4>Please install rtpMIDI and optionally iTunes for Windows on your computer</h4>
            </div>
        </section>
        )


    }

let image_content = (
    <div class="col-md-9 col-12 align-items-center" style={{ margin: "0px", padding: "0px" }}>
        {inner_image_content}
    </div>
)

let box_content = (
    <div class="col-md-3 col-12" >
        <div class="row align-items-center" style={{ marginRight: "0px", padding: "0px" }}>
            {inner_content}
        </div>
    </div>
)

let desc_container;

if (this.state.product_number % 2 !== 0) {
    desc_container = (
        <div class="container-fluid " style={{ backgroundColor: "#C0C0C0", color: "black"}}>
            <div class="row align-items-center">
                {image_content}
                {box_content}
            </div>
        </div>)
}
else {
    desc_container = (
        <div class="container-fluid " style={{ backgroundColor: "#181818", color: "white"}}>
            <div class="row align-items-center">
                {box_content}
                {image_content}
            </div>
        </div>)
}

return (
    <div>
        {desc_container}
    </div>
);



    }
}
