window.onscroll = function() {myFunction()};
var header = document.getElementById("navigation_bar");
function myFunction() {
 if (window.pageYOffset > 0) {
   header.classList.add("fixed-top");
 } else {
   header.classList.remove("fixed-top");
 }
}
