import React from 'react'
import data from './ProductFAQPage.json';
import backgroundCs from '../BackgroundColors.json';
import './ProductFAQPage.css';
import DefaultPage from '../DefaultPage'
import {styles} from '../Styles.js'

export default class ProductFAQPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = { page_id: props.page_id, submenu_id: props.submenu_id, data: data, backgroundCs: backgroundCs};
        this.styles = styles;
    }

    render() {



        const data = this.state.data;
        let page_id = this.state.page_id;
        const products = data.products;
        var product;
        var current_faqs;

        for (var i = 0; i < products.length; ++i) {
            var product_i = products[i];
            if (product_i.app_id == page_id) {
                product = product_i;
                break;
            }
        }

        if (!product || !product.faqs) {
            return (
                <DefaultPage/>
            );
        }

        current_faqs = product.faqs;

        const styles = {
            container: [
                {
                    backgroundColor: "var(--accent-bg-color)"
                },
                {
                    backgroundColor: "var(--secondary-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--third-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fourth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fifth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--sixth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--seventh-accent-bg-color)"
                },
            ]
        };


        let inner_content = current_faqs.map((list_item, i) => {
            var a_ref = "check-" + i;
            var bg_color = <text>"{backgroundCs.backgroundColors[i%8].color}"</text>;

            const styles2 = {
                container: [
                    {
                        backgroundColor: bg_color
                    }
                ]
            };

            return (
                <div>
                    <input type="checkbox" id={a_ref}></input>
                    <label for={a_ref} style={this.styles.container[i]}>
                    {list_item.question}{bg_color}
                    </label>
                    <article>
                        {list_item.answer}
                    </article>
                </div>
            )
        })

        return (
            <div style={{marginTop:"56px"}}>
            <div id="container">
                <section id="accordion">
                    {inner_content}
                </section>
            </div>
            </div>
        );


    }
}
