import React from 'react'
import data from './ProductFAQPage.json';
import './ProductFAQPage.css';

export default class ProductFAQPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = { page_id: props.page_id, submenu_id: props.submenu_id, data: data };
        this.vector = {};
    }

    render() {



        const data = this.state.data;
        let page_id = this.state.page_id;
        let submenu_id = this.state.submenu_id;
        const products = data.products;
        var product;
        var current_faqs;

        var add_id = "";
        for (var i = 0; i < products.length; ++i) {
            var product_i = products[i];
            if (product_i.app_id == page_id) {
                product = product_i;
                break;
            }
        }


        if (!product) {
            return (
                <ul class="navbar-nav w-100 justify-content-start">
                    <li class="nav-item active">
                        <a class="nav-link" href="https://www.zerodebug.com">no page found</a>
                    </li>
                </ul>
            );
        }

        current_faqs = product.faqs;

        if (!current_faqs) {
            return (
                <ul class="navbar-nav w-100 justify-content-start">
                    <li class="nav-item active">
                        <a class="nav-link" href="https://www.zerodebug.com">no page found</a>
                    </li>
                </ul>
            );
        }

        const styles = {
            container: [
                {
                    backgroundColor: "var(--accent-bg-color)"
                },
                {
                    backgroundColor: "var(--secondary-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--third-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fourth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--fifth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--sixth-accent-bg-color)"
                },
                {
                    backgroundColor: "var(--seventh-accent-bg-color)"
                },
            ]
        };


        let inner_content = current_faqs.map((list_item, i) => {
            var a_ref = "#block-1";
            if (i === 1) {
                a_ref = "#block-2";
            }
            else if (i === 2) {
                a_ref = "#block-3";
            }
            else if (i === 3) {
                a_ref = "#block-4";
            }
            else if (i === 4) {
                a_ref = "#block-5";
            }
            else if (i === 5) {
                a_ref = "#block-6";
            }
            else if (i === 6) {
                a_ref = "#block-7";
            }
            else if (i === 7) {
                a_ref = "#block-8";
            }


            return (

                <div class="col-12 col-sm-12 product_detail_box m-1" style={styles.container[i]}>
                    <a href={a_ref}>
                        <div class="row align-items-center product_detail_box py-0">
                            <div class="col-12 col-sm-12 p-0 p-sm-3 align-items-start text-center text-sm-left inner-product-box">
                                <h4 class="text_color_black align-items-start">
                                    {list_item.question}
                                </h4>
                                <h4 class="text_color_black align-items-start">
                                    {list_item.answer}
                                </h4>
                                <div>
                                    <input type="checkbox" id="check-1" ></input>
                                    <label for="check-1">{list_item.question}
                                    </label>
                                    <article>
                                        {list_item.answer}
                                    </article>
                                </div>

                            </div>
                        </div>
                    </a>
                </div>
            )
        })




        return (
            <div id="container">
                {inner_content}
                <section id="accordion">
                    <div>
                        <input type="checkbox" id="check-1"></input>
                        <label for="check-1">Can I get a discount for
				</label>
                            <article>
						In the App Store developers can't declare "upgrade-prices", so there is no way for us to give  for a discounted price to the users of the old touchAble v.3 app.
						A quantum of solace: There will be ongoing support for touchAble v.3 ...
				</article>
			</div>
		</section>
	</div>
                                );


                            }
                        }
