import React from 'react'
import '../../App.css';
import ProductLandingPage from './ProductLandingPage'
import ManualPage from './ManualPage'


export default class  PageContent extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            page_id: this.props.page_id, 
            submenu_id: this.props.submenu_id
        };
      }



    render() {
        let page_id = this.state.page_id;
        let submenu_id = this.state.submenu_id;

        if (submenu_id=== "landing_page") {
        return (
            <ProductLandingPage page_id ={page_id} submenu_id={submenu_id} />
            );
        }
        else if (submenu_id=== "manual") {
            return (
                <ManualPage page_id ={page_id} submenu_id={submenu_id} />
                );
            }
        else {
            return (
        <div class="main">
        </div>
            );
        }
}
}
